import './createroom.css'
import {useState} from 'react';


function CreateRoom({uuid}) {

    const [roomID, setRoomId] = useState(uuid());
    const [name,setName] = useState("");

    const createRoomHandler = (e) => {

        e.preventDefault();
         
        const roomData  = {
            name,
            roomID,
            userId: uuid(),
            host: true,
            presenter: true,
        };

        console.log(roomData);
    }

    return (
        <form className='form cold-md-12 mt-5'>
            <div className='form-group'>
            <input type="text" value={name} onChange={(e)=>setName(e.target.value)}className='form-control my-2' placeholder='Enter Your Name'/>
            </div>
            <div className='form- border-1'>
             <div className='input-group d-flex align-items-center justify-content-center'>
                <input type="text" value={roomID} className='form-control my-2 border-0' onClick={()=>setRoomId(uuid())} disabled placeholder='Generate Room Code'/>
                <div className='input-group-append'>
                    <button className='btn btn-primary btn-sm me-1' type="button">Generate</button>
                    <button className='btn btn-outline-danger btn-sm me-2' type="button">Copy</button>
                </div>
             </div>
            </div>   
            <button type="submit" onClick={createRoomHandler}className='mt-4 btn-primart btn-block form-control'>Generate Room</button>
            
        </form>
    )
}

export default CreateRoom;
