import React from 'react'
import { useEffect, useState } from "react";

const Chat = ({setOpenChatTab, socket}) => {

    const [chat, setChat] = useState([]);
  const [message, setMessage] = useState("");

    useEffect(() => {
        socket.on("messageResponse", (data) => {
          setChat((prevChats) => [...prevChats, data]);
        });
      }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (message.trim() !== "") {
          setChat((prevChats) => [...prevChats, { message, name: "You" }]);
          socket.emit("message", { message });
          setMessage("");
        }
      };

  return (
    <div 
      className="position-fixed top-0 h-100 text-white bg-dark " 
      style={{width:"400px",left:"0%"}}>

    <button 
      type="button" 
      onClick={() => setOpenChatTab(false)} 
      className="btn btn-light btn-block w-100 mt-5"
      >Close</button>

    <div className="mt-5 p-2 border border-1 border-white rounded-3"
        style={{ height: "70%", width:"95%",marginLeft:"2%" }}>
    {chat}
    </div>

    <form onSubmit={handleSubmit} className="w-100 mt-4 d-flex rounded-3 ">
        <input
          type="text"
          placeholder="Enter message"
          className="h-100 border-0 rounded-0 py-2 px-4"
          style={{
            width: "90%",
          }}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button type="submit" className="btn btn-primary rounded-0">
          Send
        </button>
      </form>



  </div>
  )
}

export default Chat;
