import './Canvas.css';
import { useState } from 'react';


function Canvas() {

    const [tool,setTool] = useState("pencil");


    return(
        <div className="room">
            <h1 className='text-center py-5'>WHITE BOARD SHARING</h1>
            <div className="col-md-12 mt-4 mb-5 d-flex align-items-center justify-content-around">
              <div className="d-flex col-md-4 justify-content-between gap-1">
                <div className="d-flex gap-1">
                    <label for="pencil">Pencil</label>
                    <input type="radio" name='tool' value="pencil" onChange={(e)=>setTool(e.target.value)}/>
                </div>

                <div className="d-flex gap-1">
                    <label for="line">Pencil</label>
                    <input type="radio" name='tool' value="pencil" onChange={(e)=>setTool(e.target.value)}/>
                </div>

                <div className="d-flex gap-1">
                    <label for="pencil">Pencil</label>
                    <input type="radio" name='tool' value="pencil" onChange={(e)=>setTool(e.target.value)}/>
                </div>
                
               
              </div>
            </div>
        </div>
    );

}

export default Canvas;
