import './Canvas.css';
import { useState,useEffect , useLayoutEffect} from 'react';

import rough from "roughjs/bundled/rough.esm.js";

const roughGenerator = rough.generator();


function Canvas({canvasRef, ctxRef, elements, setElements,tool}) {

    const [draw, setDraw] = useState(false);
    
    useEffect(() => {

        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        ctxRef.current = ctx;
    },[]);

    useLayoutEffect(()=> {
        const roughGen = rough.canvas(canvasRef.current);
        
        elements.forEach(element => {roughGen.linearPath(element.path);
            
        });
    },[elements]);


    const handleMouseDown = (e) => {
        const {offsetX, offsetY} = e.nativeEvent;
         console.log(offsetX,offsetY);

         if(tool==="pencil"){
            setElements((prev)=>[
                ...prev,
                {
                    type:"pencil",
                    offsetX,
                    offsetY,
                    path:[[offsetX,offsetY]],
                    stroke:"black"
                },
             ])

         }

       
         setDraw(true);

        
    }

    const handleMouseMove = (e) => {
        const {offsetX, offsetY} = e.nativeEvent;
        if(draw){
            const {path} = elements[elements.length - 1];
            const newPath = [...path,[offsetX,offsetY]];

            setElements((prev) => 
                prev.map((ele,index) => {
                    if(index === elements.length-1){
                        return {
                           ...ele,
                           path: newPath,
                        };
                    }
                    else{
                        return ele;
                    }
                } ))

        }
        
       
    }

    const handleMouseUp = (e) => {
        setDraw(false);
        const {offsetX, offsetY} = e.nativeEvent;
        console.log(offsetX,offsetY);
        
    }

    return(
    <>
     <canvas 
       ref={canvasRef}
       onMouseDown={handleMouseDown}
       onMouseMove={handleMouseMove}
       onMouseUp={handleMouseUp}

       className='border border-dark border-2 h-100 w-100'
    > </canvas>
    </>
    );

}

export default Canvas;
