import './Canvas.css';
import { useState,useEffect , useLayoutEffect} from 'react';

import rough from "roughjs/bundled/rough.esm.js";


const roughGenerator = rough.generator();


const Canvas = ({canvasRef, ctxRef, elements, setElements,tool, color,user,socket}) => {


    const [img, setImg] = useState(null);
    const [draw, setDraw] = useState(false);

    useEffect(() => {
      socket.on("whiteBoardDataResponse", (data) => {
        setImg(data.imgURL);
      });
    }, []);
    
    if (!user?.presenter) {
        return (
          <div className="border border-dark border-3 h-100 w-100 overflow-hidden">
            <img
            src={img}
              alt="Real time white board shared by presenter"
              // className="w-100 h-100"
              style={{
                height: window.innerHeight * 2,
                width: "285%",
              }}
            />
          </div>
        );
      }

  
 
    
    useEffect(() => {

        const canvas = canvasRef.current;
        if(!canvas) return;
        canvas.height = window.innerHeight*2;
        canvas.width = window.innerWidth*2; 
        const ctx = canvas.getContext("2d");

        ctx.strokeStyle = color;
        ctx.lineWidth=2;
        ctx.lineCap="round";

        ctxRef.current = ctx;
    },[]);

   

    useEffect(() => {
        ctxRef.current.strokeStyle = color;
    },[color]);

    
    useLayoutEffect(()=> {
      
        if(canvasRef){
        const roughGen = rough.canvas(canvasRef.current);

        if(elements.length > 0){
            ctxRef.current.clearRect(
               0,
               0,
              canvasRef.current.width,
              canvasRef.current.height
              );
          }
      
        
        elements.forEach(element => {
            if(element.type==="rect")
            roughGen.draw(roughGenerator.rectangle(
                element.offsetX,
                element.offsetY,
                element.width,
                element.height,
                {
                    stroke: element.stroke,
                    strokeWidth: 5,
                    roughness:0.5,
                }
                ));

            else if(element.type==="pencil")
            roughGen.linearPath(
                element.path,
                {
                    stroke: element.stroke,
                    strokeWidth: 5,
                    roughness:0,
                }
                );

            else if(element.type==="line")
            roughGen.draw(roughGenerator.line(
                element.offsetX,
                element.offsetY,
                element.width,
                element.height,
                {
                    stroke: element.stroke,
                    strokeWidth: 5,
                    roughness:0,
                }
                
                ));

            
        });

        const canvasImage = canvasRef.current.toDataURL();
        socket.emit("whiteboardData",canvasImage);
    }
    
    },[elements]);

   

    const handleMouseDown = (e) => {
        const {offsetX, offsetY} = e.nativeEvent;
         console.log(offsetX,offsetY);

         if(tool==="rect"){
            setElements((prev) => [
                ...prev,
                {
                 type: "rect",
                 offsetX,
                 offsetY,
                 width: 0,
                 height: 0,
                 stroke:color,
                },
             ]);

         }
         else if(tool==="pencil"){
            setElements((prev)=>[
                ...prev,
                {
                    type:"pencil",
                    offsetX,
                    offsetY,
                    path:[[offsetX,offsetY]],
                    stroke:color,
                },
             ])
         }
         else if(tool==="line"){

            setElements((prev) => [
                ...prev,
                {
                    type:"line",
                    offsetX,
                    offsetY,
                    width: offsetX,
                    height: offsetY,
                    stroke:color,
                }
            ])
         }

     
         
         setDraw(true);

        
    }

    const handleMouseMove = (e) => {
        const {offsetX, offsetY} = e.nativeEvent;
        if(draw){
            if(tool==="pencil"){
                const {path} = elements[elements.length - 1];
                const newPath = [...path,[offsetX,offsetY]];
    
                setElements((prev) => 
                    prev.map((ele,index) => {
                        if(index === elements.length-1){
                            return {
                               ...ele,
                               path: newPath,
                            };
                        }
                        else{
                            return ele;
                        }
                    } ))

            }
            else if(tool==="line"){

                setElements((prev) => 
                prev.map((ele,index) => {
                    if(index === elements.length-1){
                        return {
                           ...ele,
                           width: offsetX,
                           height: offsetY,
                        };
                    }
                    else{
                        return ele;
                    }
                } ))

            }
           

        else if(tool ==="rect"){
            setElements((prevElements) =>
              prevElements.map((ele, index) => {
               if(index === elements.length - 1){
                   return {
                       ...ele,
                       width:  offsetX - ele.offsetX,
                       height: offsetY - ele.offsetY,
                   };
               }
               else{
                   return ele;
               }
              })
            )
        
            }  
    }
    }
    
    const handleMouseUp = (e) => {
        setDraw(false);
        const {offsetX, offsetY} = e.nativeEvent;
        console.log(offsetX,offsetY);
        
    }



 
    
    return ( 
        <div
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        className="border border-dark border-3 h-100 w-100 overflow-hidden"> 
        
      
      <canvas 
      ref={canvasRef}
        />
    
     </div>
      );

}

export default Canvas;
